_start:
    mov edx, 13         ; Tamanho da mensagem
    mov ecx, msg        ; Ponteiro para a mensagem
    mov ebx, 1          ; File descriptor (stdout)
    mov eax, 4          ; syscall: sys_write
    int 0x80            ; Chamada de interrupção do sistema

    mov eax, 1          ; syscall: sys_exit
    xor ebx, ebx        ; Código de saída 0
    int 0x80            ; Chamada de interrupção do sistemasection .data
    msg db 'Ola, mundo!', 0Ah  ; Mensagem a ser exibida

